mapper.select:
  SELECT 
  
  ,b.DEP_NAME 
FROM CHECK_MESSAGE a LEFT JOIN DEPARTMENT b ON a.DEPARTMENT_ID=b.DEP_NUMBER 

 
  a.DEPARTMENT_ID=#{departmentId} 
 
 
  AND a.STAFF_NUMBER=#{staffNumber} 
 
 
  AND a.NAME=#{name}   
 
 
  AND a.TAGE=#{tage}   
 
 
 ORDER BY a.STAFF_NUMBER
